package com.tukeping.leetcode;

/*
 * @lc app=leetcode.cn id=76 lang=java
 *
 * [76] 最小覆盖子串
 *
 * https://leetcode-cn.com/problems/minimum-window-substring/description/
 *
 * algorithms
 * Hard (35.66%)
 * Likes:    380
 * Dislikes: 0
 * Total Accepted:    28.3K
 * Total Submissions: 79.7K
 * Testcase Example:  '"ADOBECODEBANC"\n"ABC"'
 *
 * 给你一个字符串 S、一个字符串 T，请在字符串 S 里面找出：包含 T 所有字母的最小子串。
 *
 * 示例：
 *
 * 输入: S = "ADOBECODEBANC", T = "ABC"
 * 输出: "BANC"
 *
 * 说明：
 *
 *
 * 如果 S 中不存这样的子串，则返回空字符串 ""。
 * 如果 S 中存在这样的子串，我们保证它是唯一的答案。
 */

import org.junit.Test;

import java.util.HashMap;
import java.util.Map;

import static org.hamcrest.core.Is.is;
import static org.junit.Assert.assertThat;

/**
 * hash-table | two-pointers | string | sliding-window
 *
 * facebook | linkedin | snapchat | uber
 *
 * @author tukeping
 * @date 2020/3/19
 **/
public class LeetCode76 {

    public String minWindow(String s, String t) {
        if (s.length() == 0 || t.length() == 0) return "";

        Map<Character, Integer> dictT = new HashMap<>();
        for (int i = 0; i < t.length(); i++) {
            int count = dictT.getOrDefault(t.charAt(i), 0);
            dictT.put(t.charAt(i), count + 1);
        }

        int required = dictT.size();
        int l = 0, r = 0;
        int formed = 0;

        Map<Character, Integer> windowCounts = new HashMap<>();
        int[] ans = {-1, 0, 0};

        while (r < s.length()) {
            char c = s.charAt(r);
            int count = windowCounts.getOrDefault(c, 0);
            windowCounts.put(c, count + 1);

            if (dictT.containsKey(c) && windowCounts.get(c).equals(dictT.get(c))) {
                formed++;
            }

            while (l <= r && formed == required) {
                c = s.charAt(l);
                if (ans[0] == -1 || r - l + 1 < ans[0]) {
                    ans[0] = r - l + 1;
                    ans[1] = l;
                    ans[2] = r;
                }

                windowCounts.put(c, windowCounts.get(c) - 1);
                if (dictT.containsKey(c) && windowCounts.get(c) < dictT.get(c)) {
                    formed--;
                }
                l++;
            }
            r++;
        }

        return ans[0] == -1 ? "" : s.substring(ans[1], ans[2] + 1);
    }

    /**
     * 输入: S = "ADOBECODEBANC", T = "ABC"
     * 输出: "BANC"
     */
    @Test
    public void test1() {
        String s = minWindow("ADOBECODEBANC", "ABC");
        assertThat(s, is("BANC"));
    }

    @Test
    public void test2() {
        String s = minWindow("a", "a");
        assertThat(s, is("a"));
    }

    @Test
    public void test3() {
        String s = minWindow("aa", "aa");
        assertThat(s, is("aa"));
    }

    @Test
    public void test4() {
        String s = minWindow("a", "aaa");
        assertThat(s, is(""));
    }

    @Test
    public void test5() {
        String s = minWindow("acbbaca", "aba");
        assertThat(s, is("baca"));
    }

    @Test
    public void test6() {
        String s = minWindow("", "");
        assertThat(s, is(""));
    }
}
