import org.junit.Test;

import static org.junit.Assert.assertFalse;
import static org.junit.Assert.assertTrue;

/**
 * binary-search | dynamic-programming | greedy
 *
 * Unknown
 *
 * @author tukeping
 * @date 2020/2/27
 **/
public class LeetCode392 {

    public boolean isSubsequenceTwoPoint(String s, String t) {
        int m = s.length(), n = t.length();
        int p1 = 0, p2 = 0;
        while (p1 < m && p2 < n) {
            if (s.charAt(p1) == t.charAt(p2)) {
                p1++;
            }
            p2++;
        }
        return p1 == m;
    }

    public boolean isSubsequence(String s, String t) {
        // corner case
        if (s == null || s.isEmpty()) return true;
        if (t == null || t.isEmpty()) return false;

        int p1 = -1, p2, tFromIndex = 0, sLen = s.length();

        for (int sIdx = 0; sIdx < sLen; sIdx++) {
            if (p1 == -1) {
                p1 = t.indexOf(s.charAt(sIdx), tFromIndex);
            }
            if (p1 == -1) {
                return false;
            }

            tFromIndex = p1;

            if (sIdx == sLen - 1) {
                break;
            }
            p2 = t.indexOf(s.charAt(sIdx + 1), tFromIndex + 1);
            if (p2 == -1) {
                return false;
            }

            if (p2 <= p1) {
                return false;
            }

            tFromIndex = p2;
            p1 = p2;
        }

        return true;
    }

    @Test
    public void test1() {
        boolean b = isSubsequence("abc", "ahbgdc");
        assertTrue(b);
    }

    @Test
    public void test2() {
        boolean b = isSubsequence("axc", "ahbgdc");
        assertFalse(b);
    }

    @Test
    public void test3() {
        boolean b = isSubsequence("leeeeetcode", "");
        assertTrue(b);
    }
}
